mapper.insert:
	  	INSERT INTO projects(
	  		title
	  		, deleted
	  		, progress
	  		, owner_id
	  		, template
	  		, strt_date
	  		, end_date
	  		, is_tight_prj
	  		, description
	  		, group_id
	  		, tag_name
	  		, is_access_private
	  		, status
	  		, is_done
	  		, created_at
	  		, updated_at
	  		, created_by
	  		, updated_by
	  		)
  		VALUES (
  			#{title}
  			, #{deleted}
  			, #{progress}
	  		, #{owner_id}
	  		, #{template}
	  		, #{strtDate}
	  		, #{endDate}
	  		, #{is_tight_prj}
	  		, #{description}
	  		, #{group_id}
	  		, #{tag_name}
	  		, #{is_access_private}
	  		, #{status}
	  		, #{is_done}
  			, date_trunc('second', now()::timestamp)
  			, date_trunc('second', now()::timestamp)
  			, #{created_by}
  			, #{updated_by}
  			)

mapper.update:
UPDATE projects
SET title=#{title}
	, owner_id=#{owner_id}
	, template=#{template}
	, strt_date=#{strtDate}
	, end_date=#{endDate}
	, description=#{description}
	, group_id=#{group_id}
	, tag_name=#{tag_name}
	, updated_at=date_trunc('second', now()::timestamp)
	, updated_by=#{updated_by}
	, is_tight_prj=#{is_tight_prj}
	, is_access_private=#{is_access_private}
WHERE id = #{id}
